import styled, { css } from 'styled-components';
import PropTypes from 'prop-types';

const textStyle = (element) => ({
  h1: () => css`
    font-size: 4.2rem;
    color: var(--color-brand-1);
    line-height: 1.2;
    @media(min-width: 520px) {
      font-size: 6.4rem;
    }
  `,
  h2: () => css`
    font-size: 2.8rem;
    color: var(--color-brand-1);
    @media(min-width: 720px) {
      font-size: 4rem;
    }
  `,
  h3: () => css`
    font-size: 3.6rem;
  `,
  h4: () => css`
    font-size: 2.8rem;
    color: var(--color-brand-1);
  `,
  h5: () => css`
    font-size: 2.2rem;
    color: var(--color-brand-1);
  `,
  h6: () => css`
    font-size: 1.8rem;
    color: var(--color-brand-1);
  `,
  p: () => css`
    font-size: 1.8rem;
  `,
  span: () => css`
    margin: 0;
  `,
  strong: () => css`
    margin: 0;
    font-weight: bolder;
  `,
  em: () => css`
    margin: 0;
    font-style: italic;
  `,
  small: () => css`
    font-size: 1.4rem;
  `,
}[element]());

const Text = styled.p`
  margin: 1rem 0;
  line-height: 1.5;
  ${({ as }) => as && textStyle(as)}
  ${({ transform }) => transform && css`
    text-transform: ${transform};
  `}
  ${({ align }) => align && css`
    text-align: ${align};
  `}
  ${({ size }) => size && css`
    font-size: ${size};
  `}
  ${({ weight }) => weight && css`
    font-weight: ${weight};
  `}
  ${({ truncate }) => truncate === 'break' && css`
    word-break: break-word;
  `}
  ${({ truncate }) => truncate === 'ellipsis' && css`
    width: 100%;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
  `}
`;

Text.propTypes = {
  as: PropTypes.string,
  transform: PropTypes.string,
  align: PropTypes.string,
};

export default Text;
